SELECT
  aaa.hhtfirmid,
  aaa.HHT最新净资产
FROM
  (SELECT
     CASE WHEN a.fund_id IS NULL
       THEN b.fund_id
     WHEN a.fund_id IS NOT NULL
       THEN a.fund_id END             AS hhtfirmid,
     CASE WHEN a.hhtnetin IS NULL
       THEN b.hhtasset
     WHEN b.hhtasset IS NULL
       THEN a.hhtnetin
     ELSE a.hhtnetin + b.hhtasset END AS HHT最新净资产
   FROM
     (SELECT
        fund_id,
        sum(charge_amount) AS hhtnetin
      FROM NSIP_ACCOUNT.TB_NSIP_ACCOUNT_CHARGE_ORDER@LINK_NSIP_ACCOUNT
      WHERE to_char(trade_date, 'yyyymmdd') > to_char(sysdate - 1, 'yyyymmdd')
      GROUP BY fund_id) a                                                               --HHT当日净入金
     FULL JOIN
     (SELECT
        fund_id,
        sum(last_capital) AS hhtasset
      FROM NSIP_ACCOUNT.TB_NSIP_A_FUNDS_AFTER_SETTLE@LINK_NSIP_ACCOUNT
      WHERE to_char(trade_date, 'yyyymmdd') = to_char(SYSDATE - 1, 'yyyymmdd')
        AND ORDER_STATUS = 3 AND RECONC_STATUS = 2
      GROUP BY fund_id
     ) b                                                               --HHT前日净资产
