SELECT
  aaa.pmecfirmid,
  aaa.广贵最新净资产
FROM
  (SELECT
     (CASE WHEN a.firmid IS NULL
       THEN b.firmid
      WHEN a.firmid IS NOT NULL
        THEN a.firmid END)                 AS pmecfirmid,
     CASE WHEN a.pmec_net_in IS NULL
       THEN b.net_assets
     WHEN b.net_assets IS NULL
       THEN a.pmec_net_in
     ELSE a.pmec_net_in + b.net_assets END AS 广贵最新净资产
   FROM
     (
       SELECT
         firmid,
         sum(CASE WHEN inorout = 'A'
           THEN inoutmoney
             ELSE -inoutmoney END) pmec_net_in
       FROM silver_njs.history_transfer@silver_std
       WHERE fdate > to_char(sysdate - 1, 'yyyymmdd')
       GROUP BY firmid) a         --广贵当日净入金
     FULL OUTER JOIN
     (SELECT
        firmid,
        net_assets
      FROM silver_njs.tb_silver_data_center@silver_std
      WHERE hdate = to_char(sysdate - 1, 'yyyymmdd') AND net_assets IS NOT NULL
     ) b                      --广贵前日净资产
       ON a.firmid = b.firmid
  ) aaa
WHERE aaa.pmecfirmid IN
      ('163000000006638', '163000000432418', '163000000394649', '163000000451425', '163000000479224', '163000000421923', '163000000356860', '163000000272467', '163000000003040', '163000000434879', '163000000268840', '163000000003858', '163000000474206', '163000000484684', '163000000041194', '163000000253987', '163000000004547', '163000000464194', '163000000289037', '0', '163000000370682', '163000000152251', '163000000032631', '163000000475209', '163000000475032', '163000000492262', '163000000430546', '163000000491663', '163000000496852', '163000000150359', '163000000451590', '163000000479903', '163000000037638', '163000000519446'
      )


